class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({type:"POST",url:$(e).attr("href")}).done((function(t){let a=parseInt($(e).attr("data-likes"));1==t.data.deleted?a-=1:a+=1,$(e).attr("data-likes",a),$(e).html(`<i class="bi bi-heart-fill"></i><span>&nbsp;${a}</span>`),0===a?$(e).addClass("text-black-50"):$(e).removeClass("text-black-50")})).fail((function(t){}))}))}}